import React, { useState } from "react";
import "./App.css";

function App() {
  const [available, setAvailable] = useState(0);
  const [song, setsong] = useState("");
  const [artist, setartist] = useState("");

  const handleSong = (e) => {
    setsong(e.target.value);
    console.log(song);
  };

  const handleArtist = (e) => {
    setartist(e.target.value);
    console.log(artist);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const new_song = song.split(" ");
    var final_song = "";
    for (var i = 0; i < new_song.length - 1; ++i) {
      final_song += new_song[i] + "%20";
    }

    final_song += new_song[new_song.length - 1];
    console.log(final_song);

    // const req = await fetch(
    //   `http://api.musixmatch.com/ws/1.1/matcher.lyrics.get?q_track=${final_song}&q_artist=${artist}`
    // );
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="songName"
          placeholder="song name"
          onChange={handleSong}
        />
        <input
          type="text"
          name="artistName"
          placeholder="artist name"
          onChange={handleArtist}
        />
        <button>Get lyrics</button>
      </form>
    </div>
  );
}

export default App;
