import React, { useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [available, setAvailable] = useState(0);
  const [song, setsong] = useState("");
  const [artist, setartist] = useState("");
  const [req, setReq] = useState({});
  const [lyrics, setlyrics] = useState("");

  const handleSong = (e) => {
    setsong(e.target.value);
  };

  const handleArtist = (e) => {
    setartist(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const new_song = song.split(" ");
    var final_song = "";
    for (var i = 0; i < new_song.length - 1; ++i) {
      final_song += new_song[i] + "%20";
    }

    final_song += new_song[new_song.length - 1];
    const url = `https://api.musixmatch.com/ws/1.1/matcher.lyrics.get?q_track=${final_song}&q_artist=${artist}&apikey=1e1f22ab46674c90529e1a35be559d02`;
    const req = axios.get(url);
    req.then((res) => res.json());
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="songName"
          placeholder="song name"
          onChange={handleSong}
        />
        <input
          type="text"
          name="artistName"
          placeholder="artist name"
          onChange={handleArtist}
        />
        <button>Get lyrics</button>
      </form>

      <div className="app_lyrics">{req.message.body.lyrics.lyrics_body}</div>
    </div>
  );
}

export default App;

